            Title,
            Author,
            Date,
            SubTitle,
            Quantity,
            Price,
            Description,
            Images : imgPath[2],
            Category,
        }
        console.log(newItem);

        if (checkValidations()) {
            Swal.fire("Please Enter Valid Details!");
          } else {
             axios
            .post("http://localhost:8070/items/add",newItem)
