import React, { useState } from "react";
// import axios from "axios";
import { Link } from "react-router-dom";

export default function Category(props) {


  return (

    <div class="container h-100">
    <div class="d-flex justify-content-center h-100">
      <div class="searchbar" style={{ marginBottom: 'auto', marginTop: 'auto', height: '40px', backgroundColor: 'white', 
        padding: '0px 10px 0px 10px',  width: '100%', border: '1px solid #3FC1C9',boxShadow: '1px 1px  #888888'}}>
        <input class="search_input" type="text" name=""  style={{ color: '#364F6B', border: '0', background: 'none',outline:'0', 
        width: '80%',lineHeight: '40px',transition: 'width 0.4s linear'}}/>
        <a type="button" class="search_icon" style={{height: '40px', width: '30px', float: 'right', display: 'flex',
            justifyContent: 'center',  alignItems: 'center', borderRadius: '50%',color:'#F5F5F5',  textDecoration:'none'}}>

          <img id="img141" src="./assets/images/images Used in Project Management UI Design\loupe.png"  />

        </a>
      </div>
    </div>
  </div>


  );
}
