                <h3>Books</h3>
                <br/>

        {item.map((i)=>{
            return(
                <div className="col ">
                
                    
                    <img  src = {p3}  className="img-fluid" alt="Responsive " style={{width:'50%'}}/>
                    <br/>
                    <span>{i.topic}</span>
                    <div className="col">
                    &ensp;&ensp;
                    <a href="/edit" id="link">
                         <img id="img141" src={p10} style={{width:'10%'}}/>
                    </a>
                    &ensp;&ensp;
                    <img id="img141" src={p11} style={{width:'10%'}}/>
                    </div>
                   
                </div>
            )
        })}

                <div className="row text-center" style={{fontWeight:'bold'}}>
         
                    <div className="col ">
                      
                        <img  src = {p3}  className="img-fluid" alt="Responsive " style={{width:'50%'}}/>
                        <br/>
                        <span>HellFiree</span>
                        <div className="col">
                        &ensp;&ensp;
                    
                         <a href="/edit" id="link">
                         <img id="img141" src={p10} style={{width:'10%'}}/>
                         </a>
                      
                         &ensp;&ensp;
                         <img id="img141" src={p11} style={{width:'10%'}}/>
                        </div>
                       
                    </div>

                    <div className="col ">
                    
                           
                        <img  src = {p4}  className="img-fluid" alt="Responsive " style={{width:'50%'}}/>
                        <br/>
                        <span>PlayToys</span>
                        <div className="col">
                        &ensp;&ensp;
                        <a href="/edit" id="link">
                         <img id="img141" src={p10} style={{width:'10%'}}/>
