                         &ensp;&ensp;
                         <img id="img141" src={p11} style={{width:'10%'}}/>
                        </div>
                     
                    </div>

                    <div className="col ">
                    
                           
                        <img  src = {p5}  className="img-fluid" alt="Responsive " style={{width:'50%'}}/>
                        <br/>
                        <span>Dune</span>
                        <div className="col">
                        &ensp;&ensp;
                        <a href="/edit" id="link">
                         <img id="img141" src={p10} style={{width:'10%'}}/>
                         </a>
                         &ensp;&ensp;
                         <img id="img141" src={p11} style={{width:'10%'}}/>
                        </div>
                    
                    </div>

                    <div className="col ">
                    
                           
                        <img  src = {p6}  className="img-fluid" alt="Responsive " style={{width:'50%'}}/>
                        <br/>
                        <span>Michel</span>
                        <div className="col">
                        &ensp;&ensp;
                        <a href="/edit" id="link">
                         <img id="img141" src={p10} style={{width:'10%'}}/>
                         </a>
                         &ensp;&ensp;
                         <img id="img141" src={p11} style={{width:'10%'}}/>
                        </div>
       
                    </div>

                    
                </div>

                <br/>

                <div className="row text-center" style={{fontWeight:'bold'}}>
                    
                <div className="col ">
                    
                           
                        <img  src = {p7}  className="img-fluid" alt="Responsive " style={{width:'50%'}}/>
                        <br/>
                        <span>Lord Of the Rings</span>
                        <div className="col">
                        &ensp;&ensp;
                        <a href="/edit" id="link">
                         <img id="img141" src={p10} style={{width:'10%'}}/>
                         </a>
                         &ensp;&ensp;
                         <img id="img141" src={p11} style={{width:'10%'}}/>
                        </div>
                     
                    </div>
                    <div className="col ">
                    
                           
                        <img  src = {p8}  className="img-fluid" alt="Responsive " style={{width:'50%'}}/>
                        <br/>
                        <span>Kite Runner</span>
                        <div className="col">
                        &ensp;&ensp;
                        <a href="/edit" id="link">
                         <img id="img141" src={p10} style={{width:'10%'}}/>
                         </a>
                         &ensp;&ensp;
                         <img id="img141" src={p11} style={{width:'10%'}}/>
                        </div>
                     
                    </div>
                    <div className="col ">
                    
                           
                        <img  src = {p3}  className="img-fluid" alt="Responsive " style={{width:'50%'}}/>
                        <br/>
                        <span>{title}</span>
                        <div className="col">
                        &ensp;&ensp;
                        <a href="/edit" id="link">
                         <img id="img141" src={p10} style={{width:'10%'}}/>
                         </a>
                         &ensp;&ensp;
                         <img id="img141" src={p11} style={{width:'10%'}}/>
                        </div>
                      
                    </div>
                    <div className="col ">
                   
