import React, {useState} from "react";
import Swal from "sweetalert2";
import axios from "axios";

export default function AddItems(){

    const[Title,setTitle] = useState("");
    const[Author,setAuthor] = useState("");
    const[Date,setDate] = useState("");
    const[SubTitle,setSubTitle] = useState("");
    const[Quantity,setQuantity] = useState("");
    const[Price,setPrice] = useState("");
    const[Category,setCategory] = useState("");
    const[Images,setImages] = useState("");
    const[Description,setDescription] = useState("");
    const[ItemAvailabilityStatus,setItemAvailabilityStatus] = useState("");

    let [errorMsg,setErrorMsg] = useState("");
    let [succMsg, setSuccMsg] = useState("");
    let [flag1, setFlag1] = useState(0);
    
    let [Error2Msg, setError2Msg] = useState("");
  
    let flag3 = 0;

    function sendData(e){

        e.preventDefault();

        // customerid  = localStorage.getItem("CustomerID");
             
        const newItem = {
            Title,
            Author,
            Date,
            SubTitle,
            Quantity,
            Price,
            Description,
            Images,
            Category,
            // CustomerID
        }
    
       
        console.log(newItem);
  
        axios.post("http://localhost:8070/items/add",newItem).then(()=>{
  
          setTitle(" ");
          setAuthor(" ");
          setDate(" ");
          setQuantity(" ");
          setPrice(" ");
          setSubTitle(" ");
          setDescription(" ");
          setImages(" ");
          setCategory(" ");

        
        
            Swal.fire({
              title: "Good job!",
              text: "You send the messege!",
              icon: "success",
              button: "ok!"
              
          });
          
       
          
        }).catch((err) =>{
          alert(err)
          
          setErrorMsg(err.response.data.error);
        })
      }

      function checkValidations() {
        let checkQuantity = document.getElementById("quantity").value;
        let checkModel = document.getElementById("model").value;
        let checkPrice = document.getElementById("price").value;
    
        let checkFlag1 = 1;
    
    
        if (checkQuantity <= 0 || checkQuantity > 100) {
          console.log("q");
          return true;
        }
        if (checkPrice <= 0 || checkPrice > 1000) {
          console.log("p");
          return true;
        }
        if (checkFlag1 == 1) {
          console.log("s");
          return true;
        }
      }


    return(

    <div className="items">
        <div className="container" style={{width:'80%'}}>
            <br/><br/>
            <h2 className="text-center" style={{color:'#3F3232', fontWeight:'bold'}}>Add a New Item</h2>
            <br/><br/>
            <form onSubmit = {sendData} id="create-course-form">
